const btnSortear=document.getElementById("sortear"),btnLimpar=document.getElementById("limpar"),qtdNumbers=document.getElementById("qtdNumber"),range1=document.getElementById("rangenumber1"),range2=document.getElementById("rangenumber2");function Limpar(){for(var e=document.querySelector(".showResults");e.firstChild;)e.removeChild(e.firstChild);range1.value="",range2.value="",qtdNumbers.value=""}function Sortear(t,e,r){let n="",a="",o="";var u=[];a=e<r?(n=r,e):(n=e,r);for(let e=0;e<t;e++)(o=Math.round(Math.random()*n))<=n&&o>=a&&u.push(o);for(;u.length<t;)for(let e=0;e<t-u.length;e++)(o=Math.round(Math.random()*n))<=n&&o>=a&&u.push(o);u.sort(),showInfo(u)}function showInfo(e){var t=document.querySelector(".showResults");let r="";e.forEach(e=>{r+=`<p class="results">${e}</p>`}),t.innerHTML=r}btnLimpar.addEventListener("click",e=>{e.preventDefault(),Limpar()}),btnSortear.addEventListener("click",e=>{e.preventDefault(),Sortear(parseInt(qtdNumbers.value),parseInt(range1.value),parseInt(range2.value))});